(function($){$('.requirements-list').on('change','.select-trigger-type',function(){var trigger_type=$(this).val();var id_input=$(this).siblings('.youtube-video-id');if(trigger_type==='gamipress_youtube_watch_specific_video'){id_input.show()}else{id_input.hide()}});$('.requirements-list li').each(function(){var trigger_type=$(this).find('.select-trigger-type').val();var id_input=$(this).find('.youtube-video-id');if(trigger_type==='gamipress_youtube_watch_specific_video'){id_input.show()}else{id_input.hide()}});$('.requirements-list').on('update_requirement_data','.requirement-row',function(e,requirement_details,requirement){if(requirement_details.trigger_type==='gamipress_youtube_watch_specific_video'){requirement_details.youtube_video_id=requirement.find('.youtube-video-id input').val()}})})(jQuery)